import React, { Component } from 'react';
import '../css/components/ProductCarousel.css';
import { CarouselProvider, Slider, ButtonBack, ButtonNext } from 'pure-react-carousel';
import 'pure-react-carousel/dist/react-carousel.es.css';
import ProductCard from './catalogue/ProductCard';
import throttle from 'lodash.throttle';

class ProductCarousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      recommendations_count: 0,
      recommendations: [],
      windowWidth: undefined,
      showItemsCount: 4
    };
    this.handleResize = this.handleResize.bind(this);

    // const isMobile = window.innerWidth < 480;
    // const showItems = isMobile ? 1 : 3;

    const breakpointTablet = 600;
    const breakpointMobile = 480;

    // if (this.state.windowWidth !== undefined) {
    //   if (this.state.windowWidth > breakpointMobile && this.state.windowWidth < breakpointTablet) {
    //     this.setState({ showItemsCount: 3 });
    //   }
    //   if (this.state.windowWidth <= breakpointMobile) {
    //     this.setState({ showItemsCount: 1 });
    //   }
    // }

    // this.throttledHandleWindowResize = this.throttledHandleWindowResize.bind(this);
  }

  // throttledHandleWindowResize = () => {
  //   return throttle(() => {
  //     this.setState({ windowWidth: window.innerWidth })
  //   }, 200);
  // }

  handleResize() {
    this.setState({ windowWidth: window.innerWidth });
  }

  componentDidMount() {
    fetch(`/api/products/recommendations`)
      .then(response => response.json())
      .then(data => this.setState( {recommendations_count: data.count, recommendations: data.hits} ));

    // window.addEventListener('resize', this.throttledHandleWindowResize);
    // console.log('width:', this.state.windowWidth);

    window.addEventListener('resize', this.handleResize);
    this.handleResize();
  }

  // componentWillUnmount() {
  //   // window.removeEventListener('resize', this.throttledHandleWindowResize);
  // }

  render() {
    const { recommendations_count, showItemsCount, recommendations } = this.state;
    const { name } = this.props;

    console.log('state:', this.state);

    const ProductRecommendations = recommendations.map((item, key) =>
      <ProductCard
        key= {key}
        product_id= {item.product_id}
        name= {item.product_name}
        main_image_src= {item.image ? item.image.link : ''}
        main_image_alt= {item.image ? item.image.alt : ''}
        currency= {item.currency}
        selling_price= {item.price}
      />
    );

    return (
      <div className="ProductCarousel">
        <header className="ListingHeader">
          <h1>{name}</h1>

          <CarouselProvider
            naturalSlideWidth={100}
            naturalSlideHeight={150}
            totalSlides={recommendations_count}
            visibleSlides={showItemsCount}
          >
            <Slider>
              {ProductRecommendations}

              {/* <Slide index={0}>I am the first Slide.</Slide>
              <Slide index={1}>I am the second Slide.</Slide>
              <Slide index={2}>I am the third Slide.</Slide>
              <Slide index={3}>I am the fourth Slide.</Slide>
              <Slide index={4}>I am the fith Slide.</Slide>
              <Slide index={5}>I am the sixth Slide.</Slide>
              <Slide index={6}>I am the seventh Slide.</Slide>
              <Slide index={7}>I am the eighth Slide.</Slide>
              <Slide index={8}>I am the nineth Slide.</Slide>
              <Slide index={9}>I am the tenth Slide.</Slide> */}
            </Slider>
            <ButtonBack>Back</ButtonBack>
            <ButtonNext>Next</ButtonNext>
          </CarouselProvider>

          {/* {ProductRecommendations} */}
        </header>
      </div>
    );
  }
}

export default ProductCarousel;
