import React, { Component } from 'react';
import '../../css/components/catalogue/ProductRecommendations.css';

class ProductRecommendations extends Component {
  constructor(props) {
    super(props);
    this.state = {
      test: 'dan',
      products: []
    };
  }

  componentDidMount() {
    fetch(`/api/products/recommendations`)
    .then(response => response.json())
    .then(productsData => this.setState( {products: productsData} ));
  }

  render() {
    const { test } = this.state;

    return (
      <div className="ProductRecommendations">
        <header className="ListingHeader">
          <p>{test}</p>
        </header>
      </div>
    );
  }
}

export default ProductRecommendations;
