import React, { Component } from 'react';
import '../../css/components/catalogue/ProductRecommendations.css';

class ProductRecommendations extends Component {
  constructor(props) {
    super(props);
    this.state = {
      recommendations: []
    };
  }

  componentDidMount() {
    fetch(`/api/products/recommendations`)
    .then(response => response.json())
    .then(data => this.setState( {recommendations: data.hits} ));
  }

  render() {
    const { recommendations } = this.state;

    const items = recommendations.map((item, key) =>
      <li key={item.product_id}>{item.product_name}</li>
    );

    return (
      <div className="ProductRecommendations">
        <header className="ListingHeader">
          <h1>We Recommend</h1>
          <ul>
            {items}
          </ul>
        </header>
      </div>
    );
  }
}

export default ProductRecommendations;
