import React, { Component } from 'react';
import '../css/components/ProductCarousel.css';
import { CarouselProvider, Slider, Slide, ButtonBack, ButtonNext } from 'pure-react-carousel';
import 'pure-react-carousel/dist/react-carousel.es.css';
import ProductCard from './catalogue/ProductCard';

class ProductCarousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      recommendations_count: 0,
      recommendations: []
    };
  }

  componentDidMount() {
    fetch(`/api/products/recommendations`)
      .then(response => response.json())
      .then(data => this.setState( {recommendations_count: data.count, recommendations: data.hits} ));
  }

  render() {
    const { recommendations_count, recommendations } = this.state;
    const { name } = this.props;

    console.log('state:', this.state);

    const ProductRecommendations = recommendations.map((item, key) =>
      <ProductCard
        key= {key}
        product_id= {item.product_id}
        name= {item.product_name}
        main_image_src= {item.image ? item.image.link : ''}
        main_image_alt= {item.image ? item.image.alt : ''}
        currency= {item.currency}
        selling_price= {item.price}
      />
    );

    return (
      <div className="ProductCarousel">
        <header className="ListingHeader">
          <h1>{name}</h1>

          <CarouselProvider
            naturalSlideWidth={100}
            naturalSlideHeight={250}
            totalSlides={recommendations_count}
            visibleSlides={4}
          >
            <Slider>
              {ProductRecommendations}

              {/* <Slide index={0}>I am the first Slide.</Slide>
              <Slide index={1}>I am the second Slide.</Slide>
              <Slide index={2}>I am the third Slide.</Slide>
              <Slide index={3}>I am the fourth Slide.</Slide>
              <Slide index={4}>I am the fith Slide.</Slide>
              <Slide index={5}>I am the sixth Slide.</Slide>
              <Slide index={6}>I am the seventh Slide.</Slide>
              <Slide index={7}>I am the eighth Slide.</Slide>
              <Slide index={8}>I am the nineth Slide.</Slide>
              <Slide index={9}>I am the tenth Slide.</Slide> */}
            </Slider>
            <ButtonBack>Back</ButtonBack>
            <ButtonNext>Next</ButtonNext>
          </CarouselProvider>

          {/* {ProductRecommendations} */}
        </header>
      </div>
    );
  }
}

export default ProductCarousel;
